import React from "react";
import Table from "./table";

class Restaurants extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      restaurants: [],
      nbRestaurants: 0,
      pageSize: 50,
      indexOfPage: 0
    };
  }

  componentDidMount() {
    this.getRestaurants();
  }

  getRestaurants() {
    var url = "http://localhost:8080/api/restaurants?page=" +
      this.state.indexOfPage +
      "&pagesize=" +
      this.state.pageSize;

    fetch(url)
      .then(res => res.json())
      .then(
        result => {
          this.setState({

            nbRestaurants: result.count,
            restaurants: result.data
          });
        },
        // Remarque : il est important de traiter les erreurs ici
        // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
        // des exceptions provenant de réels bugs du composant.
        error => {
          this.setState({
            error
          });
        }
      );
  }

  indexPage = indexPagechild => {
    this.state.indexOfPage = indexPagechild;
    this.getRestaurants();
  };

  pageSize = pageSizeChild => {
    this.state.pageSize = pageSizeChild;
    this.getRestaurants();
  };


  render() {
    return (
      <div>
        <Table
          titre={"TP Restaurants"}
          data={this.state.restaurants}
          rowsPerPage={this.state.pageSize}
          parrentCallbackIndexPage={index => this.indexPage(index)}
          parrentCallbackPageSize={size => this.pageSize(size)}
          nbRestaurant={this.state.nbRestaurants}
        ></Table>
      </div>
    );
  }
}
export default Restaurants;
