import React from "react";
import Table from "./table";

class Restaurants extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      restaurants: [],
      nouveauRestaurant: "",
      search: "",
      nbRestaurants: 0,
      pageSize: 50,
      indexPage: 0
    };
  }

  componentDidMount() {
    this.getRestaurants();
  }

  callbackPageSize = pageSizeChild => {
    this.state.pageSize = pageSizeChild;
    this.getRestaurants();
  };

  callbackIndexPage = indexPagechild => {
    this.state.indexPage = indexPagechild;
    this.getRestaurants();
  };


  getRestaurants() {
    var url = "http://localhost:8080/api/restaurants?page=" +
      this.state.indexPage +
      "&pagesize=" +
      this.state.pageSize +
      "&name=" +
      this.state.search;

    fetch(url)
      .then(res => res.json())
      .then(
        result => {
          this.setState({

            nbRestaurants: result.count,
            restaurants: result.data
          });
        },
        // Remarque : il est important de traiter les erreurs ici
        // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
        // des exceptions provenant de réels bugs du composant.
        error => {
          this.setState({
            error
          });
        }
      );
  }


  render() {
    return (
      <div>
        <Table
          titre={"TP Restaurants"}
          data={this.state.restaurants}
          rowsPerPage={this.state.pageSize}
          parrentCallbackIndexPage={ip => this.callbackIndexPage(ip)}
          parrentCallbackPageSize={ps => this.callbackPageSize(ps)}
          nbRestaurant={this.state.nbRestaurants}
        ></Table>
      </div>
    );
  }
}
export default Restaurants;
